import React, { useState } from "react";
import axios from "axios";
//import ForecastDate from "./ForecastDate";

export default function Forecast(props) {
  let [ready, setReady] = useState(false);
  let [forecastData, setForecastData] = useState(null);

  function showForecast(response) {
    setForecastData(response.data.daily);
    setReady(true);
  }
  //let imgUrl0 = `https://openweathermap.org/img/wn/${forecastData[0].weather[0].icon}@2x.png`;
  //let imgUrl1 = `https://openweathermap.org/img/wn/${forecastData[1].weather[1].icon}@2x.png`;
  //let imgUrl2 = `https://openweathermap.org/img/wn/${forecastData[2].weather[2].icon}@2x.png`;
  //let imgUrl3 = `https://openweathermap.org/img/wn/${forecastData[3].weather[3].icon}@2x.png`;
  //let imgUrl4 = `https://openweathermap.org/img/wn/${forecastData[4].weather[4].icon}@2x.png`;
  if (ready) {
    return (
      <div className="forecast">
        <div className="row forecast">
          <div className="col">
            <ul>
              <li className="sat">{forecastData[0].dt}</li>
              <li className="satEmoji"> {forecastData[0].weather[0].icon}</li>
              <li className="satTemperature">
                {" "}
                {forecastData[0].temp.day} â„ƒ{" "}
              </li>{" "}
            </ul>
          </div>
        </div>
      </div>
    );
  } else {
    let apiKeySec = "d4b7a77de22e4c5dc546f41665d594cb";
    let apiUrlOneCall = `https://api.openweathermap.org/data/2.5/onecall?lat=${props.coord.lat}&lon=${props.coord.lon}&appid=${apiKeySec}&units=metric`;
    axios.get(`${apiUrlOneCall}`).then(showForecast);
    return "Loading";
  }
}
