import React, { useState } from "react";

export default function Units(props) {
  let [temp, setTemp] = useState(props.tempDefault);
  let [city, setCity] = useState(props.cityDefault);

  function farenheitTemp(event) {
    event.preventDefault();
    setTemp(Math.round(props.tempDefault * 9) / 5 + 32);
    setCity(props.cityDefault);
  }
  function celsiusTemp(event) {
    event.preventDefault();
    setTemp(Math.round(props.tempDefault));
    setCity(props.cityDefault);
  }
  if ({ temp } === props.tempDefault && { city } === props.cityDefault) {
    return (
      <span className="currentTemperature">
        {Math.round((props.tempDefault * 9) / 5 + 32)}
        <sup>
          <a href="/" onClick={celsiusTemp}>
            ℃
          </a>
        </sup>
        <sup>|</sup>
        <sup id="degF">
          <a href="/" onClick={farenheitTemp}>
            ℉
          </a>{" "}
        </sup>{" "}
      </span>
    );
  } else {
    return (
      <span className="currentTemperature">
        {Math.round(temp)}
        <sup>
          <a href="/" onClick={celsiusTemp}>
            ℃
          </a>
        </sup>
        <sup>|</sup>
        <sup id="degF">
          <a href="/" onClick={farenheitTemp}>
            ℉
          </a>{" "}
        </sup>{" "}
      </span>
    );
  }
}
