import React, { useState } from "react";
import axios from "axios";
import ForecastDate from "./ForecastDate";

export default function Forecast(props) {
  let [ready, setReady] = useState(false);
  let [forecastData, setForecastData] = useState(null);

  function showForecast(response) {
    setForecastData(response.data.daily);
    setReady(true);
  }
  let imgUrl0 = `https://openweathermap.org/img/wn/${forecastData[0].weather[0].icon}@2x.png`;
  let imgUrl1 = `https://openweathermap.org/img/wn/${forecastData[1].weather[1].icon}@2x.png`;
  let imgUrl2 = `https://openweathermap.org/img/wn/${forecastData[2].weather[2].icon}@2x.png`;
  let imgUrl3 = `https://openweathermap.org/img/wn/${forecastData[3].weather[3].icon}@2x.png`;
  let imgUrl4 = `https://openweathermap.org/img/wn/${forecastData[4].weather[4].icon}@2x.png`;
  if (ready) {
    return (
      <div className="forecast">
        <div className="row forecast">
          <div className="col">
            <ul>
              <li className="sat">
                <ForecastDate forecastDate={forecastData[0].dt * 1000} />
              </li>
              <li className="satEmoji">
                {" "}
                <img src={imgUrl0} alt="" />
              </li>
              <li className="satTemperature"> {forecastData[0].temp.day} ℃ </li>{" "}
            </ul>
          </div>

          <div className="col">
            <ul>
              <li className="sun">
                <ForecastDate forecastDate={forecastData[1].dt * 1000} />
              </li>
              <li className="sunEmoji">
                <img src={imgUrl1} alt="" />
              </li>
              <li className="sunTemperature"> {forecastData[1].temp.day} ℃ </li>{" "}
            </ul>
          </div>

          <div className="col">
            <ul>
              <li className="mon">
                <ForecastDate forecastDate={forecastData[2].dt * 1000} />
              </li>
              <li className="monEmoji">
                <img src={imgUrl2} alt="" />
              </li>
              <li className="monTemperature"> {forecastData[2].temp.day} ℃ </li>{" "}
            </ul>
          </div>

          <div className="col">
            <ul>
              <li className="tue">
                <ForecastDate forecastDate={forecastData[3].dt * 1000} />
              </li>
              <li className="tueEmoji">
                <img src={imgUrl3} alt="" />
              </li>
              <li className="tueTemperature"> {forecastData[3].temp.day} ℃ </li>{" "}
            </ul>
          </div>

          <div className="col">
            <ul>
              <li className="wed">
                <ForecastDate forecastDate={forecastData[4].dt * 1000} />
              </li>
              <li className="wedEmoji">
                <img src={imgUrl4} alt="" />
              </li>
              <li className="wedTemperature"> {forecastData[4].temp.day} ℃ </li>{" "}
            </ul>
          </div>
        </div>
      </div>
    );
  } else {
    let apiKeySec = "d4b7a77de22e4c5dc546f41665d594cb";
    let apiUrlOneCall = `https://api.openweathermap.org/data/2.5/onecall?lat=${props.coord.lat}&lon=${props.coord.lon}&appid=${apiKeySec}&units=metric`;
    axios.get(`${apiUrlOneCall}`).then(showForecast);
    return "Loading";
  }
}
